// src/components/Chat.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './hat.css';  // Import custom CSS for styling

// Connect to the server (replace with your actual server URL if needed)
const socket = io('http://localhost:5000');

const Chat = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [userName, setUserName] = useState('');
  const [typing, setTyping] = useState(false);

  // Listen for incoming messages
  useEffect(() => {
    socket.on('receive_message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, msg]);
    });

    socket.on('typing', (data) => {
      setTyping(data);
    });

    return () => {
      socket.off('receive_message');
      socket.off('typing');
    };
  }, []);

  // Handle sending messages
  const sendMessage = (e) => {
    e.preventDefault();
    if (message && userName) {
      socket.emit('send_message', { userName, message });
      setMessages((prevMessages) => [...prevMessages, { userName, message }]);
      setMessage('');
    }
  };

  // Handle typing event
  const handleTyping = (e) => {
    setMessage(e.target.value);
    if (e.target.value) {
      socket.emit('typing', true);
    } else {
      socket.emit('typing', false);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h2>Chat Application</h2>
        <input
          type="text"
          placeholder="Enter your name"
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
          className="username-input"
        />
      </div>

      <div className="messages-container">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.userName === userName ? 'sent' : 'received'}`}>
            <strong>{msg.userName}: </strong>{msg.message}
          </div>
        ))}
      </div>

      {typing && <div className="typing-indicator">Someone is typing...</div>}

      <form className="chat-form" onSubmit={sendMessage}>
        <input
          type="text"
          placeholder="Type your message..."
          value={message}
          onChange={handleTyping}
          className="message-input"
        />
        <button type="submit" className="send-button">Send</button>
      </form>
    </div>
  );
};

export default Chat;
