// src/components/Chat.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

// Connect to the server (replace with your actual server URL if needed)
const socket = io('http://localhost:5000');

const Chat = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const [userName, setUserName] = useState('');

  // Listen for incoming messages
  useEffect(() => {
    socket.on('receive_message', (msg) => {
      setMessages((prevMessages) => [...prevMessages, msg]);
    });

    // Cleanup the socket listener when the component unmounts
    return () => socket.off('receive_message');
  }, []);

  // Send a message to the server
  const sendMessage = (e) => {
    e.preventDefault();
    if (message.trim() !== '') {
      const msg = {
        user: userName || 'Anonymous',  // Default to 'Anonymous' if no userName is set
        message,
      };
      socket.emit('send_message', msg);  // Send the message to the server
      setMessages((prevMessages) => [...prevMessages, msg]);  // Update local state
      setMessage('');
    }
  };

  return (
    <div style={{ marginTop: '20px', padding: '10px' }}>
      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Enter your name"
          value={userName}
          onChange={(e) => setUserName(e.target.value)}
          style={{ padding: '10px', marginRight: '10px' }}
        />
      </div>
      <div>
        <div
          style={{
            height: '300px',
            overflowY: 'scroll',
            border: '1px solid #ddd',
            padding: '10px',
            marginBottom: '20px',
            backgroundColor: '#f4f4f4',
          }}
        >
          {messages.map((msg, index) => (
            <div key={index} style={{ marginBottom: '10px' }}>
              <strong>{msg.user}:</strong> {msg.message}
            </div>
          ))}
        </div>
        <form onSubmit={sendMessage}>
          <input
            type="text"
            placeholder="Type a message..."
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            style={{ padding: '10px', width: '80%' }}
          />
          <button type="submit" style={{ padding: '10px', width: '18%' }}>
            Send
          </button>
        </form>
      </div>
    </div>
  );
};

export default Chat;
